  let insertUserDetails = `INSERT INTO user_details(user_id,phone,address,pincode,city,state)VALUES('${req.user.userId}','${phone}','${address}','${pincode}','${city}','${state}')`;

  sql.query(insertUserDetails, (err, result) => {
